actionConfiguration.body:
(dateString = '2023-01') => {
  const dateParts = dateString.split("-");
  const year = dateParts[0];
  const month = new Date(dateString + "-01").toLocaleString('default', {
    month: 'long'
  });
  const formattedDate = `${month} ${year}`;
  return formattedDate;
}

