customFusionChartConfig:
{
  "type": "stackedcolumn2dlinedy",
  "dataSource": {
    "dataset": [
			{
				"seriesname": "New",
				"color": "#16b8a0",
				"data": {{ NewMRRbyMo.data.map((row) => { return { value: row.mrr, color: Color.getRandomColor( '#16b8a0' ) } }); }}
			},
			{
				"seriesname": "Voluntary Churn",
				"color": "#e65a5b",
				"data": {{ VoluntaryChurnbyMo.data.map((row) => { return { value: -row.mrr, color: Color.getRandomColor( '#e65a5b' ) } }); }}
			},
			{
				"seriesname": "Delinquent Churn",
				"color": "#fc9479",
				"data": {{ DelinquentChurnbyMo.data.map((row) => { return { value: -row.mrr, color: Color.getRandomColor( '#fc9479' ) } }); }}
			},
			{
      	"seriesname": "Growth",
      	"plottooltext": "Growth Rate: <b>$dataValue</b>",
      	"renderAs": "line",
				"color": "#1B2E43",
				"parentyaxis": "S",
      	"data": {{ CalcGrowthRate.growthRate().map( value => ({ value }))}}
			}
		],
		"categories": [
			{
				"category": {{ ExistingMRRbyMo.data.map((row) => { return { label: row.month } }); }}
			}
		],
    "chart": {
        "caption": "Growth Rate",
        "xAxisName": "Year",
        "yAxisName": "Revenue",
				"numberPrefix": "$",
        "theme": "fusion",
        "paletteColors": "#17a2b8",
        "baseFont": "Roboto, sans-serif",
        "baseFontSize": "14",
        "baseFontColor": "#4d4d4d",
        "captionFont": "Roboto, sans-serif",
        "captionFontSize": "24",
        "subCaptionFont": "Roboto, sans-serif",
        "subCaptionFontSize": "18",
        "subCaptionFontColor": "#868e96",
        "anchorBgColor": "#1B2E43",
        "anchorBgHoverColor": "#1B2E43",
        "anchorHoverRadius": "7",
				"snumbersuffix": "%",
				"sYAxisMaxValue": "10",
				"sYAxisMinValue": "-10",
				"labelFontColor": "#888"
    }
  }
}
