customFusionChartConfig:
{
  "type": "stackedcolumn2dline",
  "dataSource": {
    "dataset": [
			{
				"seriesname": "Existing",
				"data": {{ ExistingMRRbyMo.data.map((row) => { return { value: row.mrr, color: Color.getRandomColor( '#5aa4c1' ) } }); }}
			},
			{
				"seriesname": "New",
				"color": "#16b8a0",
				"data": {{ NewMRRbyMo.data.map((row) => { return { value: row.mrr } }); }}
			},
			{
				"seriesname": "Voluntary Churn",
				"color": "#e65a5b",
				"data": {{ VoluntaryChurnbyMo.data.map((row) => { return { value: -row.mrr } }); }}
			},
			{
				"seriesname": "Delinquent Churn",
				"color": "#fc9479",
				"data": {{ DelinquentChurnbyMo.data.map((row) => { return { value: -row.mrr } }); }}
			},
			{
      	"seriesname": "Net",
      	"plottooltext": "Net MRR: <b>$dataValue</b>",
      	"renderAs": "line",
				"color": "#1B2E43",
      	"data": {{ CalcGrowthRate.netMRR().map( value => ({ value }))}}
			}
		],
		"categories": [
			{
				"category": {{ ExistingMRRbyMo.data.map((row) => { return { label:  row.month } }); }}
			}
		],
    "chart": {
        "caption": "MRR Overview",
        "yAxisName": "MRR",
        "numberPrefix": "$",
        "theme": "fusion",
        "paletteColors": "#17a2b8",
        "baseFont": "Roboto, sans-serif",
        "baseFontSize": "12",
        "baseFontColor": "#4d4d4d",
        "captionFont": "Roboto, sans-serif",
        "captionFontSize": "24",
        "subCaptionFont": "Roboto, sans-serif",
        "subCaptionFontSize": "18",
        "subCaptionFontColor": "#868e96",
				"anchorBgColor": "#1B2E43",
        "anchorBgHoverColor": "#1B2E43",
        "anchorHoverRadius": "7",
				"labelFontColor": "#888"
    }
  }
}
