children.customFusionChartConfig:
{
  "type": "column2d",
  "dataSource": {
    "data": {{ RenewalRatesbyMonthofYear.renewalRates().map((row) => {
      return { label: row.month_of_purchase, value: row.average_renewal_rate, 'color': Color.getRandomColor( '#6cbf83' ) };
  		});
		}},
    "chart": {
      "caption": "Renewal Rates by Month of Year",
			"theme": "fusion",
			"paletteColors": "#17a2b8",
			"baseFont": "Roboto, sans-serif",
			"baseFontSize": "12",
			"baseFontColor": "#4d4d4d",
			"captionFont": "Roboto, sans-serif",
			"captionFontSize": "24",
			"subCaptionFont": "Roboto, sans-serif",
			"subCaptionFontSize": "18",
			"subCaptionFontColor": "#868e96",
			"labelDisplay": "rotate",
      "slantLabel": "1",
			"labelFontSize": "12",
			"labelFontColor": "#888",
			"numbersuffix": "%"
    }
  }
}

{
  "type": "column2d",
  "dataSource": {
    "data": {{SubscriptionCancellations.data.map((row) => {
      return { label: row.x, value: row.y, 'color': Color.getRandomColor( '#d66360' ) };
