customFusionChartConfig:
{
  "type": "overlappedcolumn2d",
  "dataSource": {
    "dataset": [
			{
				"seriesname": "Submitted Requests",
				"color": "#3d73af",
				"data": {{ FeatureRequestsSubmittedbyYear.data.map((row) => { return { value: row.total_posts, color: Color.getRandomColor( '#3d73af' ) } }); }}
			},
			{
				"seriesname": "Completed Requests",
				"color": "#63a1e5",
				"data": {{ FeatureRequestsSubmittedbyYear.data.map((row) => { return { value: row.completed_posts, color: Color.getRandomColor( '#63a1e5' ) } }); }}
			}
		],
		"categories": [
			{
				"category": {{ FeatureRequestsSubmittedbyYear.data.map((row) => { return { label: row.year + '' } }); }}
			}
		],
    "chart": {
        "caption": "Feature Requests",
        "xAxisName": "Year",
        "theme": "fusion",
        "paletteColors": "#17a2b8",
        "baseFont": "Roboto, sans-serif",
        "baseFontSize": "14",
        "baseFontColor": "#4d4d4d",
        "captionFont": "Roboto, sans-serif",
        "captionFontSize": "24",
        "subCaptionFont": "Roboto, sans-serif",
        "subCaptionFontSize": "18",
        "subCaptionFontColor": "#868e96",
        "anchorBgColor": "#1B2E43",
        "anchorBgHoverColor": "#1B2E43",
        "anchorHoverRadius": "7",
				"snumbersuffix": "%"
    }
  }
}
