customFusionChartConfig:
{
  "type": "overlappedcolumn2d",
  "dataSource": {
    "dataset": [
			{
				"seriesname": "First 30 Days",
				"color": "#ffbc42",
				"data": {{ CustomerSupportRequestRates.data.map((row) => { return { value: row.percent_30_days, color: Color.getRandomColor( '#ffbc42' ) } }); }}
			},
			{
				"seriesname": "First Year",
				"color": "#f09838",
				"data": {{ CustomerSupportRequestRates.data.map((row) => { return { value: row.percent_1_year, color: Color.getRandomColor( '#f09838' ) } }); }}
			}
		],
		"categories": [
			{
				"category": {{ CustomerSupportRequestRates.data.map((row) => { return { label: row.year + '' } }); }}
			}
		],
    "chart": {
        "caption": "New Customer Support Rates",
        "xAxisName": "Year",
				"numberSuffix": "%",
        "theme": "fusion",
        "paletteColors": "#17a2b8",
        "baseFont": "Roboto, sans-serif",
        "baseFontSize": "14",
        "baseFontColor": "#4d4d4d",
        "captionFont": "Roboto, sans-serif",
        "captionFontSize": "24",
        "subCaptionFont": "Roboto, sans-serif",
        "subCaptionFontSize": "18",
        "subCaptionFontColor": "#868e96",
        "anchorBgColor": "#1B2E43",
        "anchorBgHoverColor": "#1B2E43",
        "anchorHoverRadius": "7",
				"snumbersuffix": "%"
    }
  }
}
